         split_minus = complex_num_str.split("-")

         if (len(split_plus) == 2) :
             real.append((split_plus[0]))

             im.append((split_plus[1][:-1]))

         elif (len(split_minus) == 2):
             real.append((split_minus[0]))
             im.append(("-"+split_minus[1][:-1]))
         else:
             real.append(("-"+split_minus[1]))
             im.append(("-"+split_minus[2][:-1]))
